            ["WordProcessingHistory"] = _wordReader.GetProcessedFiles(),
            ["AverageWordProcessingTime"] = _wordReader.GetAverageProcessingTimeMs(),
            ["ErrorCount"] = _errorLog.Count,
            ["RecentErrors"] = _errorLog.TakeLast(5).ToList()
        };
    }
}

