    {
        this.primaryProvider = primaryProvider;
        this.fallbackProvider = fallbackProvider;
        InitializeServices();
    }

    private void InitializeServices()
    {
        var providers = new List<string> { primaryProvider, fallbackProvider };

        if (providers.Contains("gmail"))
        {
