    private val mutex = Mutex()

    suspend fun initialize() {
        database.connect()
        cache.flushAll()
        messagePublisher.connect()
        searchEngine.connect()
        auditLog.add("EnterpriseDataService initialized at ${LocalDateTime.now()}")
