        mutex.withLock {
            operationCounts[operation] = operationCounts.getOrDefault(operation, 0) + 1
        }
    }

    suspend fun getSystemMetrics(): Map<String, Any> {
        return mapOf(
            "operationCounts" to operationCounts.toMap(),
            "cacheSize" to cache.size(),
