    public async Task IndexAsync<T>(string indexName, string documentId, T document) where T : class
    {
        await _semaphore.WaitAsync();
        try
        {
            var response = await _client.IndexAsync(document, idx => idx
                .Index(indexName)
                .Id(documentId));

