                    val conn = pool.getConnection("$instanceId-create")
                    conn.executePreparedStatement(query, params)
                    pool.releaseConnection("$instanceId-create")
                }
            }
        }
    }

    fun bulkUpdateUsers(updates: List<String>) {
        when (databaseType) {
