public class PayPalProcessor : PaymentProcessor
{
    private readonly string _clientId;
    private readonly string _clientSecret;
    private readonly bool _useSandbox;

    public override string PaymentType => "paypal";

    public PayPalProcessor(string clientId, string clientSecret, bool useSandbox)
    {
        _clientId = clientId;
        _clientSecret = clientSecret;
        _useSandbox = useSandbox;
    }

    public override async Task<PaymentResult> ProcessPaymentAsync(PaymentRequest request)
    {
        if (!IsValidEmail(request.Credential))
        {
            return new PaymentResult(false, "Invalid PayPal email", null, DateTime.Now);
        }

        await Task.Delay(200); // Simulate payment processing
        var transactionId = $"PP_{Guid.NewGuid().ToString().Substring(0, 8)}";
        return new PaymentResult(true, "PayPal payment successful", transactionId, DateTime.Now);
    }

    private bool IsValidEmail(string email)
    {
        try
        {
            var addr = new System.Net.Mail.MailAddress(email);
            return addr.Address == email;
        }
        catch
        {
            return false;
        }
    }
}

public class BankTransferProcessor : PaymentProcessor
{
    private readonly string _apiUrl;
    private readonly string _institutionId;

    public override string PaymentType => "banktransfer";

    public BankTransferProcessor(string apiUrl, string institutionId)
    {
        _apiUrl = apiUrl;
        _institutionId = institutionId;
    }

    public override async Task<PaymentResult> ProcessPaymentAsync(PaymentRequest request)
    {
        if (!IsValidBankAccount(request.Credential))
        {
            return new PaymentResult(false, "Invalid bank account number", null, DateTime.Now);
        }

        await Task.Delay(200); // Simulate payment processing
        var transactionId = $"BT_{Guid.NewGuid().ToString().Substring(0, 8)}";
        return new PaymentResult(true, "Bank transfer successful", transactionId, DateTime.Now);
    }

    private bool IsValidBankAccount(string accountNumber)
    {
        return !string.IsNullOrEmpty(accountNumber) && accountNumber.Length >= 10;
    }
}
