        delay(100)

        if (cache.containsKey(filePath)) {
            accessTimes[filePath] = System.currentTimeMillis()
            return cache[filePath]!!
        }

        val content = "Reading PDF content from: $filePath"
        cache[filePath] = content
        accessTimes[filePath] = System.currentTimeMillis()
        return content
    }

    fun clearCache() {
        cache.clear()
        accessTimes.clear()
    }

