        var updateQuery = $"UPDATE Users SET Name='{userData.GetValueOrDefault("Name", "")}', Email='{userData.GetValueOrDefault("Email", "")}' WHERE Id='{userId}'";
        var rowsAffected = await _database.ExecuteNonQueryAsync(updateQuery);

