    void DoWork();
}

// Concrete classes for each type of machine
class Dishwasher : IMachine {
    public void DoWork() {
        Console.WriteLine("Washing dishes...");
    }
}

class VacuumCleaner : IMachine {
    public void DoWork() {
        Console.WriteLine("Vacuuming floor...");
    }
}

// MachineWorker class is now simplified and only has a single responsibility (working with machines)
class MachineWorker {
    private List<IMachine> _machines;

    public MachineWorker(List<IMachine> machines) {
