using System;

// Interface Segregation Principle (ISP)
public interface IVehicleOperator {
    string GetName();
}

// Dependency Inversion Principle (DIP)
public abstract class Vehicle {
    public abstract void Start();
    public abstract void Stop();
}

// Open-Closed Principle (OCP)
public interface IFlyableVehicle : IVehicleOperator {
    void Fly();
}

// Liskov Substitution Principle (LSP)
public class Plane : Vehicle, IFlyableVehicle {
    private readonly Pilot _pilot;

    public Plane(Pilot pilot) {
        _pilot = pilot;
    }

    public override void Start() {
        Console.WriteLine("Plane engines started by " + _pilot.GetName());
    }

    public override void Stop() {
        Console.WriteLine("Plane stopped");
    }

    public void Fly() {
        if (_pilot.GetName().Contains("Captain")) {
            Console.WriteLine("Plane flying at cruising altitude");
        } else {
            Console.WriteLine("Plane flying");
        }
    }
}

// Single Responsibility Principle (SRP)
public class Pilot : IVehicleOperator {
    private readonly string _name;

    public Pilot(string name) {
        _name = name;
    }

    public string GetName() {
        return _name;
    }
}

