        } else {
            System.out.println(name + " has no poison left!");
        }
    }

    @Override
    public void summonMinions() {
        throw new UnsupportedOperationException("Spider cannot summon minions!");
    }

    @Override
    public void takeDamage(int[] damageValues) {
        int totalDamage = 0;
        for (int damage : damageValues) {
            if (damage > 0) {
                int amplifiedDamage = damage + (damage / 2);
                totalDamage += amplifiedDamage;

                if (damage > 10) {
                    System.out.println(name + " screeches in pain!");
