interface Vehicle {
    fun start()
    fun stop()
    fun fly()
    fun dive()
}

interface Operator {
    fun steer()
    fun navigateAir()
    fun navigateWater()
    fun communicateWithTower()
}

class Driver(private val name: String) : Operator {
    override fun steer() {
        if (name.isNotEmpty()) {
            if (name.length > 5) {
                println("Experienced driver $name steering carefully")
            } else {
                println("Driver $name steering")
            }
        }
    }

    override fun navigateAir() {
        throw UnsupportedOperationException("Driver cannot navigate air")
    }

    override fun navigateWater() {
        throw UnsupportedOperationException("Driver cannot navigate water")
    }

    override fun communicateWithTower() {
        throw UnsupportedOperationException("Driver cannot communicate with tower")
    }

    fun getName(): String = name
}

class Pilot(private val name: String) : Operator {
    override fun steer() {
        println("Pilot $name controlling aircraft")
    }

    override fun navigateAir() {
        when {
            name.contains("Captain") -> println("Captain pilot navigating complex airspace")
            name.length > 6 -> println("Experienced pilot navigating airspace")
            else -> println("Pilot navigating")
        }
    }

    override fun navigateWater() {
        throw UnsupportedOperationException("Pilot cannot navigate water")
    }

    override fun communicateWithTower() {
        println("Pilot $name communicating with control tower")
    }

    fun getName(): String = name
}

class Captain(private val name: String) : Operator {
    override fun steer() {
        println("Captain $name steering submarine")
    }

    override fun navigateAir() {
        throw UnsupportedOperationException("Captain cannot navigate air")
    }

    override fun navigateWater() {
        if (name.startsWith("Captain")) {
            println("Captain navigating deep waters")
        } else {
            println("Naval officer navigating")
        }
    }

    override fun communicateWithTower() {
        throw UnsupportedOperationException("Captain cannot communicate with tower")
    }

    fun getName(): String = name
}

class Car(private val driver: Driver) : Vehicle {
    override fun start() {
        println("Car started by ${driver.getName()}")
    }

    override fun stop() {
        println("Car stopped")
    }

    override fun fly() {
        throw UnsupportedOperationException("Car cannot fly")
    }

    override fun dive() {
        throw UnsupportedOperationException("Car cannot dive")
    }

    fun getOperator(): Driver = driver
}

class Plane(private val pilot: Pilot) : Vehicle {
    override fun start() {
        println("Plane engines started by ${pilot.getName()}")
    }

    override fun stop() {
        println("Plane stopped")
    }

    override fun fly() {
        if (pilot.getName().contains("Captain")) {
            println("Plane flying at cruising altitude")
        } else {
            println("Plane flying")
        }
    }

    override fun dive() {
        throw UnsupportedOperationException("Plane cannot dive underwater")
    }

    fun getOperator(): Pilot = pilot
}

class Submarine(private val captain: Captain) : Vehicle {
    private var depth = 0

    override fun start() {
        println("Submarine engines started by ${captain.getName()}")
    }

    override fun stop() {
        println("Submarine stopped")
    }

    override fun fly() {
        throw UnsupportedOperationException("Submarine cannot fly")
    }

    override fun dive() {
        if (captain.getName().startsWith("Captain")) {
            when {
                depth < 100 -> {
                    depth += 50
                    println("Submarine diving to depth: $depth meters")
                }
                depth < 200 -> {
                    depth += 30
                    println("Deep diving to: $depth meters")
                }
                else -> println("Maximum depth reached")
            }
        }
    }

    fun getOperator(): Captain = captain
}

