class MusicPlayer(ABC):
    @abstractmethod
    def play(self):
        pass

    @abstractmethod
    def pause(self):
        pass

    @abstractmethod
    def stop(self):
        pass

    @abstractmethod
    def skip_track(self):
        pass

    @abstractmethod
    def eject(self):
        pass

    @abstractmethod
    def set_volume(self, volume):
        pass

    @abstractmethod
    def set_equalizer(self, preset):
        pass

    @abstractmethod
    def display_track_info(self):
        pass

    @abstractmethod
    def shuffle(self):
        pass

    @abstractmethod
    def repeat(self):
        pass

class CDPlayer(MusicPlayer):
    def __init__(self):
        self.is_playing = False
        self.is_paused = False
        self.current_track = "Track 1"
        self.volume = 50
        self.shuffle_mode = False
        self.repeat_mode = False

