class RestaurantWorker(ABC):
    @abstractmethod
    def cook_food(self):
        pass

    @abstractmethod
    def serve_customers(self):
        pass

    @abstractmethod
    def clean_tables(self):
        pass

    @abstractmethod
    def manage_staff(self):
        pass

class MachineWorker(ABC):
    @abstractmethod
    def wash_dishes(self):
        pass

    @abstractmethod
    def vacuum_floor(self):
        pass

    @abstractmethod
    def self_diagnose(self):
        pass

