    def __init__(self):
        self.conflict = True

    def cook_food(self):
        print("Manager does not cook.")

    def serve_customers(self):
        print("Manager does not serve.")

    def clean_tables(self):
        print("Manager does not clean.")

    def manage_staff(self):
        if self.conflict:
            print("Manager is resolving a team conflict.")
