interface GameCharacter {
    void meleeAttack();
    void castSpell();
    void fly();
    void defend();
}

interface Weapon {
    void slash();
    void shoot();
    void enchant();
    void repair();
}

class Mage implements GameCharacter {
    private String name;
    private int mana;
    private int level;

    public Mage(String name, int level) {
        this.name = name;
        this.mana = 100;
        this.level = level;
    }

    @Override
    public void meleeAttack() {
        throw new UnsupportedOperationException("Mage cannot perform melee attacks!");
    }

    @Override
    public void castSpell() {
        if (mana < 10) {
            System.out.println(name + " is out of mana!");
            return;
        }

        if (level >= 5 && mana >= 30) {
            System.out.println(name + " casts a powerful lightning storm!");
            mana -= 30;
        } else if (level >= 3 && mana >= 20) {
            System.out.println(name + " casts a fireball spell!");
            mana -= 20;
        } else if (mana >= 10) {
            System.out.println(name + " casts a basic magic missile!");
            mana -= 10;
        }
    }

    @Override
    public void fly() {
        if (level >= 2) {
            System.out.println(name + " soars through the air with magic!");
        } else {
            System.out.println(name + " is too inexperienced to fly!");
        }
    }

    @Override
    public void defend() {
        if (mana >= 15) {
            System.out.println(name + " creates a magical barrier!");
            mana -= 15;
        } else {
            System.out.println(name + " dodges awkwardly!");
        }
    }
}

class Fighter implements GameCharacter {
    private String name;
    private int stamina;
    private boolean hasWeapon;

    public Fighter(String name, boolean hasWeapon) {
        this.name = name;
        this.stamina = 100;
        this.hasWeapon = hasWeapon;
    }

    @Override
    public void meleeAttack() {
        if (stamina < 5) {
            System.out.println(name + " is too exhausted to fight!");
            return;
        }

        if (hasWeapon) {
            if (stamina >= 20) {
                System.out.println(name + " performs a devastating combo attack!");
                stamina -= 20;
            } else if (stamina >= 10) {
                System.out.println(name + " swings weapon with force!");
                stamina -= 10;
            } else {
                System.out.println(name + " makes a weak weapon strike!");
                stamina -= 5;
            }
        } else {
            if (stamina >= 15) {
                System.out.println(name + " throws a powerful punch!");
                stamina -= 15;
            } else {
                System.out.println(name + " throws a basic punch!");
                stamina -= 5;
            }
        }
    }

    @Override
    public void castSpell() {
        throw new UnsupportedOperationException("Fighter cannot cast spells!");
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Fighter cannot fly!");
    }

    @Override
    public void defend() {
        if (hasWeapon && stamina >= 10) {
            System.out.println(name + " blocks with weapon!");
            stamina -= 10;
        } else if (stamina >= 5) {
            System.out.println(name + " raises arms to defend!");
            stamina -= 5;
        } else {
            System.out.println(name + " barely manages to dodge!");
        }
    }
}

class Sword implements Weapon {
    private int sharpness;
    private boolean isMagic;

    public Sword(int sharpness, boolean isMagic) {
        this.sharpness = sharpness;
        this.isMagic = isMagic;
    }

    @Override
    public void slash() {
        if (sharpness > 70) {
            System.out.println("Sword cuts cleanly through the target!");
        } else if (sharpness > 30) {
            System.out.println("Sword makes a decent cut!");
        } else {
            System.out.println("Dull sword barely scratches the target!");
        }
    }

    @Override
    public void shoot() {
        throw new UnsupportedOperationException("Sword cannot shoot!");
    }

    @Override
    public void enchant() {
        if (isMagic) {
            System.out.println("Magical sword glows with enhanced power!");
            sharpness += 10;
        } else {
            throw new UnsupportedOperationException("Non-magic sword cannot be enchanted!");
        }
    }

    @Override
    public void repair() {
        if (sharpness < 100) {
            sharpness = Math.min(100, sharpness + 25);
            System.out.println("Sword has been sharpened and repaired!");
        } else {
            System.out.println("Sword is already in perfect condition!");
        }
    }
}

class Bow implements Weapon {
    private int arrows;
    private int range;

    public Bow(int arrows, int range) {
        this.arrows = arrows;
        this.range = range;
    }

    @Override
    public void slash() {
        throw new UnsupportedOperationException("Bow cannot slash!");
    }

    @Override
    public void shoot() {
        if (arrows <= 0) {
            System.out.println("No arrows left to shoot!");
            return;
        }

        if (range > 80) {
            System.out.println("Long-range shot hits the distant target!");
        } else if (range > 50) {
            System.out.println("Medium-range shot finds its mark!");
        } else {
            System.out.println("Short-range shot barely reaches the target!");
        }
        arrows--;
    }

    @Override
    public void enchant() {
        throw new UnsupportedOperationException("Regular bow cannot be enchanted!");
    }

    @Override
    public void repair() {
        if (range < 100) {
            range = Math.min(100, range + 15);
            System.out.println("Bow string tightened and wood polished!");
        } else {
            System.out.println("Bow is in excellent condition!");
        }
    }
}

