            print(f"{self.name} swings sword with great force!")
            self.stamina -= 10
        else:
            print(f"{self.name} is too tired to attack!")

class Defender(ABC):
    @abstractmethod
    def defend(self):
        pass

class ShieldDefender(Defender):
    def __init__(self, name):
        self.name = name

    def defend(self):
        print(f"{self.name} raises shield to block incoming attacks!")

class GameCharacter:
