    def __init__(self, name, has_weapon):
        self.name = name
        self.stamina = 100
        self.has_weapon = has_weapon

    def melee_attack(self):
        if self.stamina < 5:
            print(f"{self.name} is too exhausted to fight!")
            return

        if self.has_weapon:
            if self.stamina >= 20:
                print(f"{self.name} performs a devastating combo attack!")
