class VacuumCleaner implements MachineWorker {
    boolean isCharged = false;
    int batteryLevel = 30;
    String[] cleaningModes = {"eco", "normal", "turbo"};

    public void washDishes() {
        System.out.println("Vacuum cleaner cannot wash dishes.");
    }

    public void vacuumFloor() {
        for (String mode : cleaningModes) {
            if (isCharged || batteryLevel > 20) {
                if (mode.equals("turbo") && batteryLevel > 50) {
                    System.out.println("Vacuum cleaner using turbo mode");
                    batteryLevel -= 15;
                } else if (mode.equals("normal") && batteryLevel > 30) {
                    for (int room = 1; room <= 3; room++) {
                        System.out.println("Vacuum cleaning room " + room + " in normal mode");
                    }
                    batteryLevel -= 10;
                } else if (mode.equals("eco")) {
                    System.out.println("Vacuum using eco mode to save battery");
                    batteryLevel -= 5;
                } else {
