    override fun slash() {
        if (sharpness > 70) {
            println("Sword cuts cleanly through the target!")
        } else if (sharpness > 30) {
            println("Sword makes a decent cut!")
        } else {
            println("Dull sword barely scratches the target!")
        }
    }

    override fun shoot() {
        throw UnsupportedOperationException("Sword cannot shoot!")
    }

    override fun enchant() {
