interface RestaurantWorker {
    fun cookFood()
    fun serveCustomers()
    fun cleanTables()
    fun manageStaff()
}

interface MachineWorker {
    fun washDishes()
    fun vacuumFloor()
    fun selfDiagnose()
}

class Chef : RestaurantWorker {
    override fun cookFood() {
        println("Chef is cooking meals.")
    }

    override fun serveCustomers() {
        println("Chef does not serve customers.")
    }

    override fun cleanTables() {
        println("Chef does not clean tables.")
    }

    override fun manageStaff() {
        println("Chef does not manage staff.")
    }
}

class Waiter : RestaurantWorker {
    override fun cookFood() {
        println("Waiter does not cook food.")
    }

    override fun serveCustomers() {
        println("Waiter is serving customers.")
    }

    override fun cleanTables() {
        println("Waiter is cleaning tables.")
    }

    override fun manageStaff() {
        println("Waiter does not manage staff.")
    }
}

class Manager : RestaurantWorker {
    private val conflict = true

    override fun cookFood() {
        println("Manager does not cook.")
    }

    override fun serveCustomers() {
        println("Manager does not serve.")
    }

    override fun cleanTables() {
        println("Manager does not clean.")
    }

    override fun manageStaff() {
        if (conflict) {
            println("Manager is resolving a team conflict.")
        } else {
            println("Manager is approving staff schedules.")
        }
    }
}

class Dishwasher : MachineWorker {
    private val dirtyItems = 15

    override fun washDishes() {
        if (dirtyItems > 0) {
            println("Dishwasher is cleaning $dirtyItems plates.")
        } else {
            println("No dishes to clean.")
        }
    }

    override fun vacuumFloor() {
        println("Dishwasher cannot vacuum the floor.")
    }

    override fun selfDiagnose() {
        println("Dishwasher performing self-diagnosis.")
    }
}

class VacuumCleaner : MachineWorker {
    private val isCharged = false

    override fun washDishes() {
        println("Vacuum cleaner cannot wash dishes.")
    }

    override fun vacuumFloor() {
        if (isCharged) {
            println("Vacuum cleaner is cleaning the floor.")
        } else {
            println("Vacuum cleaner battery is low.")
        }
    }

    override fun selfDiagnose() {
        println("Vacuum cleaner running diagnostics.")
    }
}

