from abc import ABC, abstractmethod

class UnsupportedOperationException(Exception):
    pass

class Vehicle(ABC):
    @abstractmethod
    def start(self):
        pass

    @abstractmethod
    def stop(self):
        pass

    @abstractmethod
    def fly(self):
        pass

    @abstractmethod
    def dive(self):
        pass

class Operator(ABC):
    @abstractmethod
    def steer(self):
        pass

    @abstractmethod
    def navigate_air(self):
        pass

    @abstractmethod
    def navigate_water(self):
        pass

    @abstractmethod
    def communicate_with_tower(self):
        pass

class EnergySystem(ABC):
    @abstractmethod
    def charge_electrically(self):
        pass

    @abstractmethod
    def refuel_gasoline(self):
        pass

    @abstractmethod
    def refuel_diesel(self):
        pass

    @abstractmethod
    def load_nuclear_fuel(self):
        pass

    @abstractmethod
    def check_battery_level(self):
        pass

    @abstractmethod
    def maintain_engine(self):
        pass

class Driver(Operator):
    def __init__(self, name):
        self.name = name

    def steer(self):
        if self.name is not None and len(self.name) > 0:
            if len(self.name) > 5:
                print(f"Experienced driver {self.name} steering carefully")
            else:
                print(f"Driver {self.name} steering")

    def navigate_air(self):
        raise UnsupportedOperationException("Driver cannot navigate air")

    def navigate_water(self):
        raise UnsupportedOperationException("Driver cannot navigate water")

    def communicate_with_tower(self):
        raise UnsupportedOperationException("Driver cannot communicate with tower")

    def get_name(self):
        return self.name

class Pilot(Operator):
    def __init__(self, name):
        self.name = name

    def steer(self):
        print(f"Pilot {self.name} controlling aircraft")

    def navigate_air(self):
        if self.name is not None:
            if "Captain" in self.name:
                print("Captain pilot navigating complex airspace")
            elif len(self.name) > 6:
                print("Experienced pilot navigating airspace")
            else:
                print("Pilot navigating")

    def navigate_water(self):
        raise UnsupportedOperationException("Pilot cannot navigate water")

    def communicate_with_tower(self):
        print(f"Pilot {self.name} communicating with control tower")

    def get_name(self):
        return self.name

class Captain(Operator):
    def __init__(self, name):
        self.name = name

    def steer(self):
        print(f"Captain {self.name} steering submarine")

    def navigate_air(self):
        raise UnsupportedOperationException("Captain cannot navigate air")

    def navigate_water(self):
        if self.name is not None and self.name.startswith("Captain"):
            print("Captain navigating deep waters")
        else:
            print("Naval officer navigating")

    def communicate_with_tower(self):
        raise UnsupportedOperationException("Captain cannot communicate with tower")

    def get_name(self):
        return self.name

class Car(Vehicle, EnergySystem):
    def __init__(self, driver, energy_type):
        self.driver = driver
        self.energy_type = energy_type

    def start(self):
        if self.driver is not None:
            print(f"{self.energy_type} car started by {self.driver.get_name()}")

    def stop(self):
        print("Car stopped")

    def fly(self):
        raise UnsupportedOperationException("Car cannot fly")

    def dive(self):
        raise UnsupportedOperationException("Car cannot dive")

    def charge_electrically(self):
        if self.energy_type == "Electric":
            print("Car charging electrically")
        else:
            raise UnsupportedOperationException("Non-electric car cannot charge")

    def refuel_gasoline(self):
        if self.energy_type == "Gasoline":
            print("Car refueling with gasoline")
        else:
            raise UnsupportedOperationException("Non-gasoline car cannot refuel gasoline")

    def refuel_diesel(self):
        raise UnsupportedOperationException("Car does not use diesel")

    def load_nuclear_fuel(self):
        raise UnsupportedOperationException("Car cannot use nuclear fuel")

    def check_battery_level(self):
        if self.energy_type == "Electric":
            print("Car battery level: 85%")
        else:
            raise UnsupportedOperationException("Non-electric car has no battery")

    def maintain_engine(self):
        if self.energy_type == "Electric":
            print("Maintaining electric motor")
        else:
            print("Maintaining combustion engine")

    def get_operator(self):
        return self.driver

class Plane(Vehicle, EnergySystem):
    def __init__(self, pilot, energy_type):
        self.pilot = pilot
        self.energy_type = energy_type

    def start(self):
