        else:
            print("Maintaining jet engines")

    def get_operator(self):
        return self.pilot

class Submarine(Vehicle, EnergySystem):
    def __init__(self, captain, energy_type):
        self.captain = captain
        self.depth = 0
        self.energy_type = energy_type

    def start(self):
        print(f"{self.energy_type} submarine engines started by {self.captain.get_name()}")

