    def refuel_diesel(self):
        if self.energy_type == "Diesel":
            print("Submarine refueling with marine diesel")
        else:
            raise UnsupportedOperationException("Non-diesel submarine cannot refuel diesel")

    def load_nuclear_fuel(self):
        if self.energy_type == "Nuclear":
            print("Loading nuclear fuel rods into reactor")
