from abc import ABC, abstractmethod

class Vehicle(ABC):
    @abstractmethod
    def start(self):
        pass

    @abstractmethod
    def stop(self):
        pass

    @abstractmethod
    def fly(self):
        pass

    @abstractmethod
    def dive(self):
        pass

class Operator(ABC):
    @abstractmethod
    def steer(self):
        pass

    @abstractmethod
    def navigate_air(self):
        pass

    @abstractmethod
    def navigate_water(self):
        pass

    @abstractmethod
    def communicate_with_tower(self):
        pass

class Driver(Operator):
    def __init__(self, name):
        self.name = name

    def steer(self):
        if self.name and len(self.name) > 0:
            if len(self.name) > 5:
                print(f"Experienced driver {self.name} steering carefully")
            else:
                print(f"Driver {self.name} steering")

    def navigate_air(self):
        raise NotImplementedError("Driver cannot navigate air")

    def navigate_water(self):
        raise NotImplementedError("Driver cannot navigate water")

    def communicate_with_tower(self):
        raise NotImplementedError("Driver cannot communicate with tower")

    def get_name(self):
        return self.name

class Pilot(Operator):
    def __init__(self, name):
        self.name = name

    def steer(self):
        print(f"Pilot {self.name} controlling aircraft")

    def navigate_air(self):
        if self.name:
            if "Captain" in self.name:
                print("Captain pilot navigating complex airspace")
            elif len(self.name) > 6:
                print("Experienced pilot navigating airspace")
            else:
                print("Pilot navigating")

    def navigate_water(self):
        raise NotImplementedError("Pilot cannot navigate water")

    def communicate_with_tower(self):
        print(f"Pilot {self.name} communicating with control tower")

    def get_name(self):
        return self.name

class Captain(Operator):
    def __init__(self, name):
        self.name = name

    def steer(self):
        print(f"Captain {self.name} steering submarine")

    def navigate_air(self):
        raise NotImplementedError("Captain cannot navigate air")

    def navigate_water(self):
        if self.name and self.name.startswith("Captain"):
            print("Captain navigating deep waters")
        else:
            print("Naval officer navigating")

    def communicate_with_tower(self):
        raise NotImplementedError("Captain cannot communicate with tower")

    def get_name(self):
        return self.name

class Car(Vehicle):
    def __init__(self, driver):
        self.driver = driver

    def start(self):
        if self.driver:
            print(f"Car started by {self.driver.get_name()}")
        else:
            print("Car started")

    def stop(self):
        print("Car stopped")

    def fly(self):
        raise NotImplementedError("Car cannot fly")

    def dive(self):
        raise NotImplementedError("Car cannot dive")

    def get_operator(self):
        return self.driver

class Plane(Vehicle):
    def __init__(self, pilot):
        self.pilot = pilot

    def start(self):
        print(f"Plane engines started by {self.pilot.get_name()}")

    def stop(self):
        print("Plane stopped")

    def fly(self):
        if "Captain" in self.pilot.get_name():
            print("Plane flying at cruising altitude")
        else:
            print("Plane flying")

    def dive(self):
        raise NotImplementedError("Plane cannot dive underwater")

    def get_operator(self):
        return self.pilot

class Submarine(Vehicle):
    def __init__(self, captain):
        self.captain = captain
        self.depth = 0

    def start(self):
        print(f"Submarine engines started by {self.captain.get_name()}")

    def stop(self):
        print("Submarine stopped")

    def fly(self):
        raise NotImplementedError("Submarine cannot fly")

    def dive(self):
        if self.captain and self.captain.get_name().startswith("Captain"):
            if self.depth < 100:
                self.depth += 50
                print(f"Submarine diving to depth: {self.depth} meters")
            elif self.depth < 200:
                self.depth += 30
                print(f"Deep diving to: {self.depth} meters")
            else:
                print("Maximum depth reached")

    def get_operator(self):
        return self.captain

