        else if (volume > 100)
        {
            this.volume = 100;
        }
        else
        {
            this.volume = volume;
        }
        if (this.volume > 80)
        {
            Console.WriteLine("Amplifier: Warning - High volume!");
        }
        Console.WriteLine("Amplifier: Master volume set to " + this.volume);
    }

    public void SetEqualizer(string preset)
    {
        switch (preset.ToLower())
        {
            case "rock":
            case "pop":
            case "jazz":
