    void stop();
    void fly();
    void dive();
}

interface Operator {
    void steer();
    void navigateAir();
    void navigateWater();
    void communicateWithTower();
}

interface EnergySystem {
    void chargeElectrically();
    void refuelGasoline();
    void refuelDiesel();
    void loadNuclearFuel();
    void checkBatteryLevel();
    void maintainEngine();
}

class Driver implements Operator {
    private String name;

    public Driver(String name) {
        this.name = name;
    }

    public void steer() {
        if (name != null && !name.isEmpty()) {
            if (name.length() > 5) {
                System.out.println("Experienced driver " + name + " steering carefully");
            } else {
                System.out.println("Driver " + name + " steering");
            }
        }
    }

    public void navigateAir() {
        throw new UnsupportedOperationException("Driver cannot navigate air");
    }

    public void navigateWater() {
        throw new UnsupportedOperationException("Driver cannot navigate water");
    }

    public void communicateWithTower() {
        throw new UnsupportedOperationException("Driver cannot communicate with tower");
    }

    public String getName() {
        return name;
    }
}

class Pilot implements Operator {
    private String name;

    public Pilot(String name) {
        this.name = name;
    }

    public void steer() {
        System.out.println("Pilot " + name + " controlling aircraft");
    }

    public void navigateAir() {
        if (name != null) {
            if (name.contains("Captain")) {
                System.out.println("Captain pilot navigating complex airspace");
            } else if (name.length() > 6) {
                System.out.println("Experienced pilot navigating airspace");
            } else {
                System.out.println("Pilot navigating");
            }
        }
    }

    public void navigateWater() {
        throw new UnsupportedOperationException("Pilot cannot navigate water");
    }

    public void communicateWithTower() {
        System.out.println("Pilot " + name + " communicating with control tower");
    }

    public String getName() {
        return name;
    }
}

class Captain implements Operator {
    private String name;

    public Captain(String name) {
        this.name = name;
    }

    public void steer() {
        System.out.println("Captain " + name + " steering submarine");
    }

    public void navigateAir() {
        throw new UnsupportedOperationException("Captain cannot navigate air");
    }

    public void navigateWater() {
        if (name != null && name.startsWith("Captain")) {
            System.out.println("Captain navigating deep waters");
        } else {
            System.out.println("Naval officer navigating");
        }
    }

    public void communicateWithTower() {
        throw new UnsupportedOperationException("Captain cannot communicate with tower");
    }

    public String getName() {
        return name;
    }
}

class Car implements Vehicle, EnergySystem {
    private Driver driver;
    private String energyType;

    public Car(Driver driver, String energyType) {
        this.driver = driver;
        this.energyType = energyType;
    }

    public void start() {
        if (driver != null) {
            System.out.println(energyType + " car started by " + driver.getName());
        }
    }

    public void stop() {
        System.out.println("Car stopped");
    }

    public void fly() {
        throw new UnsupportedOperationException("Car cannot fly");
    }

    public void dive() {
        throw new UnsupportedOperationException("Car cannot dive");
    }

    public void chargeElectrically() {
        if (energyType.equals("Electric")) {
            System.out.println("Car charging electrically");
        } else {
            throw new UnsupportedOperationException("Non-electric car cannot charge");
        }
    }

    public void refuelGasoline() {
        if (energyType.equals("Gasoline")) {
            System.out.println("Car refueling with gasoline");
        } else {
            throw new UnsupportedOperationException("Non-gasoline car cannot refuel gasoline");
        }
    }

    public void refuelDiesel() {
        throw new UnsupportedOperationException("Car does not use diesel");
    }

    public void loadNuclearFuel() {
        throw new UnsupportedOperationException("Car cannot use nuclear fuel");
    }

    public void checkBatteryLevel() {
        if (energyType.equals("Electric")) {
            System.out.println("Car battery level: 85%");
        } else {
            throw new UnsupportedOperationException("Non-electric car has no battery");
        }
    }

    public void maintainEngine() {
        if (energyType.equals("Electric")) {
            System.out.println("Maintaining electric motor");
        } else {
            System.out.println("Maintaining combustion engine");
        }
    }

    public Driver getOperator() {
        return driver;
    }
}

class Plane implements Vehicle, EnergySystem {
    private Pilot pilot;
    private String energyType;

    public Plane(Pilot pilot, String energyType) {
        this.pilot = pilot;
        this.energyType = energyType;
    }

    public void start() {
        System.out.println(energyType + " plane engines started by " + pilot.getName());
    }

    public void stop() {
        System.out.println("Plane stopped");
    }

    public void fly() {
        if (pilot.getName().contains("Captain")) {
            System.out.println("Plane flying at cruising altitude with " + energyType + " power");
        } else {
            System.out.println("Plane flying with " + energyType + " engines");
        }
    }

    public void dive() {
        throw new UnsupportedOperationException("Plane cannot dive underwater");
    }

    public void chargeElectrically() {
        throw new UnsupportedOperationException("Plane does not charge electrically");
    }

    public void refuelGasoline() {
        throw new UnsupportedOperationException("Plane does not use gasoline");
    }

    public void refuelDiesel() {
        if (energyType.equals("Diesel")) {
            System.out.println("Plane refueling with aviation diesel");
        } else {
            throw new UnsupportedOperationException("Non-diesel plane cannot refuel diesel");
        }
    }

    public void loadNuclearFuel() {
        throw new UnsupportedOperationException("Plane cannot use nuclear fuel");
    }

    public void checkBatteryLevel() {
        throw new UnsupportedOperationException("Plane has no battery system");
    }

    public void maintainEngine() {
        if (energyType.equals("Diesel")) {
            System.out.println("Maintaining diesel turbine engines");
        } else {
            System.out.println("Maintaining jet engines");
        }
    }

    public Pilot getOperator() {
        return pilot;
    }
}

class Submarine implements Vehicle, EnergySystem {
    private Captain captain;
    private int depth;
    private String energyType;

    public Submarine(Captain captain, String energyType) {
        this.captain = captain;
        this.depth = 0;
        this.energyType = energyType;
    }

    public void start() {
        System.out.println(energyType + " submarine engines started by " + captain.getName());
    }

    public void stop() {
        System.out.println("Submarine stopped");
    }

    public void fly() {
        throw new UnsupportedOperationException("Submarine cannot fly");
    }

    public void dive() {
        if (captain != null && captain.getName().startsWith("Captain")) {
            if (depth < 100) {
                depth += 50;
                System.out.println("Submarine diving to depth: " + depth + " meters using " + energyType + " power");
            } else if (depth < 200) {
                depth += 30;
                System.out.println("Deep diving to: " + depth + " meters");
            } else {
                System.out.println("Maximum depth reached");
            }
        }
    }

    public void chargeElectrically() {
        if (energyType.equals("Electric")) {
            System.out.println("Submarine charging batteries");
        } else {
            throw new UnsupportedOperationException("Non-electric submarine cannot charge");
        }
    }

    public void refuelGasoline() {
        throw new UnsupportedOperationException("Submarine does not use gasoline");
    }

    public void refuelDiesel() {
        if (energyType.equals("Diesel")) {
            System.out.println("Submarine refueling with marine diesel");
        } else {
            throw new UnsupportedOperationException("Non-diesel submarine cannot refuel diesel");
        }
    }

    public void loadNuclearFuel() {
        if (energyType.equals("Nuclear")) {
            System.out.println("Loading nuclear fuel rods into reactor");
        } else {
            throw new UnsupportedOperationException("Non-nuclear submarine cannot load nuclear fuel");
        }
    }

    public void checkBatteryLevel() {
        if (energyType.equals("Electric") || energyType.equals("Diesel")) {
            System.out.println("Submarine battery level: 70%");
        } else {
            throw new UnsupportedOperationException("Nuclear submarine has no battery system");
        }
    }

    public void maintainEngine() {
        if (energyType.equals("Nuclear")) {
            System.out.println("Maintaining nuclear reactor systems");
        } else if (energyType.equals("Diesel")) {
            System.out.println("Maintaining diesel-electric propulsion");
        } else {
            System.out.println("Maintaining electric propulsion system");
        }
    }

    public Captain getOperator() {
        return captain;
    }
}

