class Driver:
    def __init__(self, name):
        self.name = name

class Pilot(Driver):
    pass

class Captain(Driver):
    def communicate_with_control_room(self):
        print("Communicating with control room")

class Car:
    def __init__(self, driver):
        self.driver = driver

    def drive(self):
        print(f"{self.driver.name} is driving the car.")

class Plane:
    def __init__(self, pilot):
        self.pilot = pilot

    def fly(self):
        print(f"{self.pilot.name} is flying the plane.")

class Submarine:
    def __init__(self, captain):
        self.captain = captain

    def dive(self):
        print(f"{self.captain.name} is diving the submarine.")

