```kotlin
interface MeleeAttacker {
    fun meleeAttack()
}

interface SpellCaster {
    fun castSpell()
}

interface Flyer {
    fun fly()
}

class Mage(private val name: String) : SpellCaster, Flyer {
    private var mana: Int = 100

    override fun castSpell()  {
        if (mana >= 20) {
            println("$name casts a fireball spell!")
            mana -= 20
        } else {
            println("$name is out of mana!")
        }
