interface GameCharacter {
    fun meleeAttack()
    fun castSpell()
    fun fly()
    fun defend()
}

interface Weapon {
    fun slash()
    fun shoot()
    fun enchant()
    fun repair()
}

interface Enemy {
    fun attack()
    fun fly()
    fun poison()
    fun summonMinions()
    fun takeDamage(damageValues: IntArray)
}

class Mage(private val name: String, private val level: Int) : GameCharacter {
    private var mana: Int = 100

    override fun meleeAttack() {
        throw UnsupportedOperationException("Mage cannot perform melee attacks!")
    }

    override fun castSpell() {
        if (mana < 10) {
            println("$name is out of mana!")
            return
        }

        if (level >= 5 && mana >= 30) {
            println("$name casts a powerful lightning storm!")
            mana -= 30
        } else if (level >= 3 && mana >= 20) {
            println("$name casts a fireball spell!")
            mana -= 20
        } else if (mana >= 10) {
            println("$name casts a basic magic missile!")
            mana -= 10
        }
    }

    override fun fly() {
        if (level >= 2) {
            println("$name soars through the air with magic!")
        } else {
            println("$name is too inexperienced to fly!")
        }
    }

    override fun defend() {
        if (mana >= 15) {
            println("$name creates a magical barrier!")
            mana -= 15
        } else {
            println("$name dodges awkwardly!")
        }
    }
}

class Fighter(private val name: String, private val hasWeapon: Boolean) : GameCharacter {
    private var stamina: Int = 100

    override fun meleeAttack() {
        if (stamina < 5) {
            println("$name is too exhausted to fight!")
            return
        }

        if (hasWeapon) {
            if (stamina >= 20) {
                println("$name performs a devastating combo attack!")
                stamina -= 20
            } else if (stamina >= 10) {
                println("$name swings weapon with force!")
                stamina -= 10
            } else {
                println("$name makes a weak weapon strike!")
                stamina -= 5
            }
        } else {
            if (stamina >= 15) {
                println("$name throws a powerful punch!")
                stamina -= 15
            } else {
                println("$name throws a basic punch!")
                stamina -= 5
            }
        }
    }

    override fun castSpell() {
        throw UnsupportedOperationException("Fighter cannot cast spells!")
    }

    override fun fly() {
        throw UnsupportedOperationException("Fighter cannot fly!")
    }

    override fun defend() {
        if (hasWeapon && stamina >= 10) {
            println("$name blocks with weapon!")
            stamina -= 10
        } else if (stamina >= 5) {
            println("$name raises arms to defend!")
            stamina -= 5
        } else {
            println("$name barely manages to dodge!")
        }
    }
}

class Sword(private var sharpness: Int, private val isMagic: Boolean) : Weapon {

    override fun slash() {
        if (sharpness > 70) {
            println("Sword cuts cleanly through the target!")
        } else if (sharpness > 30) {
            println("Sword makes a decent cut!")
        } else {
            println("Dull sword barely scratches the target!")
        }
    }

    override fun shoot() {
        throw UnsupportedOperationException("Sword cannot shoot!")
    }

    override fun enchant() {
        if (isMagic) {
            println("Magical sword glows with enhanced power!")
            sharpness += 10
        } else {
            throw UnsupportedOperationException("Non-magic sword cannot be enchanted!")
        }
    }

    override fun repair() {
        if (sharpness < 100) {
            sharpness = kotlin.math.min(100, sharpness + 25)
            println("Sword has been sharpened and repaired!")
        } else {
            println("Sword is already in perfect condition!")
        }
    }
}

class Bow(private var arrows: Int, private var range: Int) : Weapon {

    override fun slash() {
        throw UnsupportedOperationException("Bow cannot slash!")
    }

    override fun shoot() {
        if (arrows <= 0) {
            println("No arrows left to shoot!")
            return
        }

        if (range > 80) {
            println("Long-range shot hits the distant target!")
        } else if (range > 50) {
            println("Medium-range shot finds its mark!")
        } else {
            println("Short-range shot barely reaches the target!")
        }
        arrows--
    }

    override fun enchant() {
        throw UnsupportedOperationException("Regular bow cannot be enchanted!")
    }

    override fun repair() {
        if (range < 100) {
            range = kotlin.math.min(100, range + 15)
            println("Bow string tightened and wood polished!")
        } else {
            println("Bow is in excellent condition!")
        }
    }
}

class Orc(private val name: String, private val strength: Int) : Enemy {
    private var health: Int = 100

    override fun attack() {
        if (strength > 50) {
            println("$name delivers a crushing blow!")
        } else {
            println("$name swings clumsily!")
        }
    }

    override fun fly() {
        throw UnsupportedOperationException("Orc cannot fly!")
    }

    override fun poison() {
        throw UnsupportedOperationException("Orc cannot poison enemies!")
    }

    override fun summonMinions() {
        throw UnsupportedOperationException("Orc cannot summon minions!")
    }

    override fun takeDamage(damageValues: IntArray) {
        var totalDamage = 0
        for (i in damageValues.indices) {
            if (damageValues[i] > 0) {
                totalDamage += damageValues[i]
                if (damageValues[i] > 20) {
                    println("$name staggers from heavy damage!")
                }
            }
        }
        health -= totalDamage
        println("$name takes $totalDamage damage! Health: $health")

        if (health <= 0) {
            println("$name has been defeated!")
        }
    }
}

class Dragon(private val name: String, private val firepower: Int) : Enemy {
    private var health: Int = 200
    private var canFly: Boolean = true

    override fun attack() {
        if (firepower > 80) {
            println("$name breathes devastating flames!")
        } else if (firepower > 50) {
            println("$name breathes hot fire!")
        } else {
            println("$name breathes weak flames!")
        }
    }

    override fun fly() {
        if (canFly && health > 50) {
            println("$name soars majestically through the sky!")
        } else if (health <= 50) {
            println("$name is too wounded to fly!")
        }
    }

    override fun poison() {
        throw UnsupportedOperationException("Dragon doesn't use poison!")
    }

    override fun summonMinions() {
        throw UnsupportedOperationException("Dragon doesn't summon minions!")
    }

    override fun takeDamage(damageValues: IntArray) {
        var totalDamage = 0
        var criticalHits = 0

        for (i in damageValues.indices) {
            if (damageValues[i] > 0) {
                val reducedDamage = kotlin.math.max(1, damageValues[i] - 5)
                totalDamage += reducedDamage

                if (damageValues[i] > 30) {
                    criticalHits++
                    println("$name roars in pain from critical hit!")
                }
            }
        }

        health -= totalDamage
        println("$name takes $totalDamage damage ($criticalHits critical hits)! Health: $health")

        if (health <= 0) {
            println("$name crashes to the ground, defeated!")
            canFly = false
        }
    }
}

class Spider(private val name: String) : Enemy {
    private var health: Int = 50
    private var hasPoison: Boolean = true

    override fun attack() {
        println("$name bites with venomous fangs!")
    }

    override fun fly() {
        throw UnsupportedOperationException("Spider cannot fly!")
    }

    override fun poison() {
        if (hasPoison) {
            println("$name injects deadly venom!")
        } else {
            println("$name has no poison left!")
        }
    }

    override fun summonMinions() {
        throw UnsupportedOperationException("Spider cannot summon minions!")
    }

    override fun takeDamage(damageValues: IntArray) {
        var totalDamage = 0
        for (damage in damageValues) {
            if (damage > 0) {
                val amplifiedDamage = damage + (damage / 2)
                totalDamage += amplifiedDamage

                if (damage > 10) {
                    println("$name screeches in pain!")
                }
            }
        }

        health -= totalDamage
        println("$name takes $totalDamage damage! Health: $health")

        if (health <= 0) {
            println("$name curls up and dies!")
        }
    }
}

