from abc import ABC, abstractmethod


class MusicPlayer(ABC):
    @abstractmethod
    def play(self):
        pass

    @abstractmethod
    def pause(self):
        pass

    @abstractmethod
    def stop(self):
        pass

    @abstractmethod
    def skip_track(self):
        pass

    @abstractmethod
    def eject(self):
        pass

