class Chef : Cook, Managerial, SupplyManager {
    private var mealsCooked = 0
    private val recipes = arrayOf("pasta", "pizza", "salad", "soup", "steak")
    private val isHeadChef = true

    override fun cookFood() {
        for (i in recipes.indices) {
            if (mealsCooked < 50) {
                if (recipes[i] == "steak" && isHeadChef) {
                    println("Head Chef is preparing premium ${recipes[i]}")
                    mealsCooked += 2
                } else if (recipes[i] == "pasta" || recipes[i] == "pizza") {
                    println("Chef is cooking ${recipes[i]}")
                    mealsCooked++
                } else {
                    println("Chef is preparing simple ${recipes[i]}")
                }
            }
        }
    }

    override fun manageStaff() {
        if (isHeadChef) {
            println("Head Chef is managing kitchen staff.")
        }
    }

    override fun orderSupplies() {
        if (isHeadChef) {
            for (i in 1..3) {
                println("Head Chef ordering kitchen supplies batch $i")
            }
        }
    }
}

class Waiter : Server, Cleaner {
    private var tablesServed = 0
    private val isExperienced = false
    private val customerTypes = arrayOf("regular", "vip", "family", "business")

    override fun serveCustomers() {
        for (customerType in customerTypes) {
            if (tablesServed < 20) {
