interface MusicPlayer {
    fun play()
    fun pause()
    fun stop()
    fun skipTrack()
    fun eject()
    fun setVolume(volume: Int)
    fun setEqualizer(preset: String)
    fun displayTrackInfo()
    fun shuffle()
    fun repeat()
}

class CDPlayer : MusicPlayer {
    private var isPlaying = false
    private var isPaused = false
    private var currentTrack = "Track 1"
    private var volume = 50
    private var shuffleMode = false
    private var repeatMode = false

    override fun play() {
        isPlaying = true
        isPaused = false
        println("CD Player: Playing $currentTrack")
    }

    override fun pause() {
        if (isPlaying) {
            isPaused = true
            isPlaying = false
            println("CD Player: Paused")
        }
    }

    override fun stop() {
        isPlaying = false
        isPaused = false
        println("CD Player: Stopped")
    }

    override fun skipTrack() {
        println("CD Player: Skipping to next track")
        val trackNumber = currentTrack.split(" ")[1].toInt() + 1
        currentTrack = "Track $trackNumber"
    }

    override fun eject() {
        stop()
        println("CD Player: CD ejected")
    }

    override fun setVolume(volume: Int) {
        this.volume = volume
        println("CD Player: Volume set to $volume")
    }

    override fun setEqualizer(preset: String) {
        println("CD Player: Equalizer set to $preset")
    }

    override fun displayTrackInfo() {
        println("CD Player: Current track - $currentTrack, Volume: $volume")
    }

    override fun shuffle() {
        shuffleMode = !shuffleMode
        println("CD Player: Shuffle ${if (shuffleMode) "ON" else "OFF"}")
    }

    override fun repeat() {
        repeatMode = !repeatMode
        println("CD Player: Repeat ${if (repeatMode) "ON" else "OFF"}")
    }
}

class VinylPlayer : MusicPlayer {
    private var isPlaying = false
    private var isPaused = false
    private var currentSong = "Side A"
    private var volume = 50

    override fun play() {
        isPlaying = true
        isPaused = false
        println("Vinyl Player: Playing $currentSong")
    }

    override fun pause() {
        if (isPlaying) {
            isPaused = true
            isPlaying = false
            println("Vinyl Player: Paused (needle lifted)")
        }
    }

    override fun stop() {
        isPlaying = false
        isPaused = false
        println("Vinyl Player: Stopped")
    }

    override fun skipTrack() {
        throw UnsupportedOperationException("Vinyl players cannot skip tracks - you must manually move the needle!")
    }

    override fun eject() {
        throw UnsupportedOperationException("Vinyl players don't eject - you must manually remove the record!")
    }

    override fun setVolume(volume: Int) {
        this.volume = volume
        println("Vinyl Player: Volume set to $volume")
    }

    override fun setEqualizer(preset: String) {
        throw UnsupportedOperationException("Vintage vinyl players don't have digital equalizers!")
    }

    override fun displayTrackInfo() {
        println("Vinyl Player: Current side - $currentSong, Volume: $volume")
    }

    override fun shuffle() {
        throw UnsupportedOperationException("Cannot shuffle vinyl records!")
    }

    override fun repeat() {
        throw UnsupportedOperationException("Vinyl players don't have automatic repeat!")
    }
}

class Amplifier : MusicPlayer {
    private var isPoweredOn = false
    private var volume = 30
    private var equalizerSetting = "Flat"
    private var bassBoost = false

    override fun play() {
        throw UnsupportedOperationException("Amplifiers don't play music - they amplify audio signals!")
    }

    override fun pause() {
        throw UnsupportedOperationException("Amplifiers don't control playback!")
    }

    override fun stop() {
        isPoweredOn = false
        println("Amplifier: Powered OFF")
    }

    override fun skipTrack() {
        throw UnsupportedOperationException("Amplifiers don't control track selection!")
    }

    override fun eject() {
        throw UnsupportedOperationException("Amplifiers don't have media to eject!")
    }

    override fun setVolume(volume: Int) {
        this.volume = when {
            volume < 0 -> 0
            volume > 100 -> 100
            else -> volume
        }

        if (this.volume > 80) {
            println("Amplifier: Warning - High volume!")
        }

        println("Amplifier: Master volume set to ${this.volume}")
    }

    override fun setEqualizer(preset: String) {
        equalizerSetting = when (preset.lowercase()) {
            "rock", "pop", "jazz", "classical" -> preset
            else -> {
                println("Amplifier: Unknown preset. Defaulting to Flat.")
                "Flat"
            }
        }
        if (equalizerSetting != "Flat" || preset.lowercase() in listOf("rock", "pop", "jazz", "classical")) {
            println("Amplifier: Equalizer set to $equalizerSetting")
        }
    }

    override fun displayTrackInfo() {
        throw UnsupportedOperationException("Amplifiers don't track music information!")
    }

    override fun shuffle() {
        throw UnsupportedOperationException("Amplifiers don't control playback modes!")
    }

    override fun repeat() {
        throw UnsupportedOperationException("Amplifiers don't control playback modes!")
    }

    fun powerOn() {
        isPoweredOn = true
        println("Amplifier: Powered ON")
    }

    fun setBassBoost(enabled: Boolean) {
        bassBoost = enabled
        println("Amplifier: Bass boost ${if (enabled) "ON" else "OFF"}")
    }
}

