class Worker(ABC):
    @abstractmethod
    def perform_task(self):
        pass

class RestaurantWorker(Worker):
    @abstractmethod
    def serve_customers(self):
        pass

class MachineWorker(Worker):
    @abstractmethod
    def perform_machine_task(self):
        pass

class Chef(RestaurantWorker):
    def __init__(self):
        self.meals_cooked = 0
        self.recipes = ["pasta", "pizza", "salad", "soup", "steak"]
        self.is_head_chef = True

    def perform_task(self):
        self.cook_food()

    def cook_food(self):
        for i in range(len(self.recipes)):
            if self.meals_cooked < 50:
                if self.recipes[i] == "steak" and self.is_head_chef:
                    print(f"Head Chef is preparing premium {self.recipes[i]}")
                    self.meals_cooked += 2
