    def enchant(self):
        raise NotImplementedError("Regular bow cannot be enchanted!")

    def repair(self):
        if self.range < 100:
            self.range = min(100, self.range + 15)
            print("Bow string tightened and wood polished!")
        else:
            print("Bow is in excellent condition!")
