interface MusicPlayer {
    void play();
    void pause();
    void stop();
    void skipTrack();
    void eject();
    void setVolume(int volume);
    void setEqualizer(String preset);
    void displayTrackInfo();
    void shuffle();
    void repeat();
}

interface AdvancedAudioDevice {
    void connectBluetooth();
    void disconnectBluetooth();
    void pairDevice(String deviceName);
    void enableMicrophone();
    void disableMicrophone();
    void firmwareUpdate();
}

class CDPlayer implements MusicPlayer {
    private boolean isPlaying = false;
    private boolean isPaused = false;
    private String currentTrack = "Track 1";
    private int volume = 50;
    private boolean shuffleMode = false;
    private boolean repeatMode = false;

    @Override
    public void play() {
        isPlaying = true;
        isPaused = false;
        System.out.println("CD Player: Playing " + currentTrack);
    }

    @Override
    public void pause() {
        if (isPlaying) {
            isPaused = true;
            isPlaying = false;
            System.out.println("CD Player: Paused");
        }
    }

    @Override
    public void stop() {
        isPlaying = false;
        isPaused = false;
        System.out.println("CD Player: Stopped");
    }

    @Override
    public void skipTrack() {
        System.out.println("CD Player: Skipping to next track");
        currentTrack = "Track " + (Integer.parseInt(currentTrack.split(" ")[1]) + 1);
    }

    @Override
    public void eject() {
        stop();
        System.out.println("CD Player: CD ejected");
    }

    @Override
    public void setVolume(int volume) {
        this.volume = volume;
        System.out.println("CD Player: Volume set to " + volume);
    }

    @Override
    public void setEqualizer(String preset) {
        System.out.println("CD Player: Equalizer set to " + preset);
    }

    @Override
    public void displayTrackInfo() {
        System.out.println("CD Player: Current track - " + currentTrack + ", Volume: " + volume);
    }

    @Override
    public void shuffle() {
        shuffleMode = !shuffleMode;
        System.out.println("CD Player: Shuffle " + (shuffleMode ? "ON" : "OFF"));
    }

    @Override
    public void repeat() {
        repeatMode = !repeatMode;
        System.out.println("CD Player: Repeat " + (repeatMode ? "ON" : "OFF"));
    }
}

class VinylPlayer implements MusicPlayer {
    private boolean isPlaying = false;
