        System.out.println("Vinyl Player: Stopped");
    }

    @Override
    public void skipTrack() {
        throw new UnsupportedOperationException("Vinyl players cannot skip tracks - you must manually move the needle!");
    }

    @Override
    public void eject() {
        throw new UnsupportedOperationException("Vinyl players don't eject - you must manually remove the record!");
    }

    @Override
    public void setVolume(int volume) {
        this.volume = volume;
        System.out.println("Vinyl Player: Volume set to " + volume);
    }

    @Override
    public void setEqualizer(String preset) {
        throw new UnsupportedOperationException("Vintage vinyl players don't have digital equalizers!");
