    @Override
    public void disableMicrophone() {
        throw new UnsupportedOperationException("Bluetooth Speaker doesn't support microphone");
    }

    @Override
    public void firmwareUpdate() {
        System.out.println("Bluetooth Speaker: Firmware updated");
    }
}

