interface MusicPlayer {
    void play();
    void pause();
    void stop();
    void skipTrack();
    void eject();
    void setVolume(int volume);
    void setEqualizer(String preset);
    void displayTrackInfo();
    void shuffle();
    void repeat();
}

class CDPlayer implements MusicPlayer {
    private boolean isPlaying = false;
    private boolean isPaused = false;
    private String currentTrack = "Track 1";
    private int volume = 50;
    private boolean shuffleMode = false;
    private boolean repeatMode = false;

    @Override
    public void play() {
        isPlaying = true;
        isPaused = false;
        System.out.println("CD Player: Playing " + currentTrack);
    }

    @Override
    public void pause() {
        if (isPlaying) {
            isPaused = true;
            isPlaying = false;
            System.out.println("CD Player: Paused");
        }
    }

    @Override
    public void stop() {
        isPlaying = false;
        isPaused = false;
        System.out.println("CD Player: Stopped");
    }

    @Override
    public void skipTrack() {
        System.out.println("CD Player: Skipping to next track");
        currentTrack = "Track " + (Integer.parseInt(currentTrack.split(" ")[1]) + 1);
    }

    @Override
    public void eject() {
        stop();
        System.out.println("CD Player: CD ejected");
    }

    @Override
    public void setVolume(int volume) {
        this.volume = volume;
        System.out.println("CD Player: Volume set to " + volume);
    }

    @Override
    public void setEqualizer(String preset) {
        System.out.println("CD Player: Equalizer set to " + preset);
    }

    @Override
    public void displayTrackInfo() {
        System.out.println("CD Player: Current track - " + currentTrack + ", Volume: " + volume);
    }

    @Override
    public void shuffle() {
        shuffleMode = !shuffleMode;
        System.out.println("CD Player: Shuffle " + (shuffleMode ? "ON" : "OFF"));
    }

    @Override
    public void repeat() {
        repeatMode = !repeatMode;
        System.out.println("CD Player: Repeat " + (repeatMode ? "ON" : "OFF"));
    }
}

class VinylPlayer implements MusicPlayer {
    private boolean isPlaying = false;
    private boolean isPaused = false;
    private String currentSong = "Side A";
    private int volume = 50;

    @Override
    public void play() {
        isPlaying = true;
        isPaused = false;
        System.out.println("Vinyl Player: Playing " + currentSong);
    }

    @Override
    public void pause() {
        if (isPlaying) {
            isPaused = true;
            isPlaying = false;
            System.out.println("Vinyl Player: Paused (needle lifted)");
        }
    }

    @Override
    public void stop() {
        isPlaying = false;
        isPaused = false;
        System.out.println("Vinyl Player: Stopped");
    }

    @Override
    public void skipTrack() {
        throw new UnsupportedOperationException("Vinyl players cannot skip tracks - you must manually move the needle!");
    }

    @Override
    public void eject() {
        throw new UnsupportedOperationException("Vinyl players don't eject - you must manually remove the record!");
    }

    @Override
    public void setVolume(int volume) {
        this.volume = volume;
        System.out.println("Vinyl Player: Volume set to " + volume);
    }

    @Override
    public void setEqualizer(String preset) {
        throw new UnsupportedOperationException("Vintage vinyl players don't have digital equalizers!");
    }

    @Override
    public void displayTrackInfo() {
        System.out.println("Vinyl Player: Current side - " + currentSong + ", Volume: " + volume);
    }

    @Override
    public void shuffle() {
        throw new UnsupportedOperationException("Cannot shuffle vinyl records!");
    }

    @Override
    public void repeat() {
        throw new UnsupportedOperationException("Vinyl players don't have automatic repeat!");
    }
}

class Amplifier implements MusicPlayer {
    private boolean isPoweredOn = false;
    private int volume = 30;
    private String equalizerSetting = "Flat";
    private boolean bassBoost = false;

    @Override
    public void play() {
        throw new UnsupportedOperationException("Amplifiers don't play music - they amplify audio signals!");
    }

    @Override
    public void pause() {
        throw new UnsupportedOperationException("Amplifiers don't control playback!");
    }

    @Override
    public void stop() {
        isPoweredOn = false;
        System.out.println("Amplifier: Powered OFF");
    }

    @Override
    public void skipTrack() {
        throw new UnsupportedOperationException("Amplifiers don't control track selection!");
    }

    @Override
    public void eject() {
        throw new UnsupportedOperationException("Amplifiers don't have media to eject!");
    }

    @Override
	public void setVolume(int volume) {
		if (volume < 0) {
			this.volume = 0;
		} else if (volume > 100) {
			this.volume = 100;
		} else {
			this.volume = volume;
		}

		if (this.volume > 80) {
			System.out.println("Amplifier: Warning - High volume!");
		}

		System.out.println("Amplifier: Master volume set to " + this.volume);
	}


    @Override
    @Override
	public void setEqualizer(String preset) {
		switch (preset.toLowerCase()) {
			case "rock":
			case "pop":
			case "jazz":
			case "classical":
				equalizerSetting = preset;
				break;
			default:
				equalizerSetting = "Flat";
				System.out.println("Amplifier: Unknown preset. Defaulting to Flat.");
				return;
		}
		System.out.println("Amplifier: Equalizer set to " + equalizerSetting);
	}


    @Override
    public void displayTrackInfo() {
        throw new UnsupportedOperationException("Amplifiers don't track music information!");
    }

    @Override
    public void shuffle() {
        throw new UnsupportedOperationException("Amplifiers don't control playback modes!");
    }

    @Override
    public void repeat() {
        throw new UnsupportedOperationException("Amplifiers don't control playback modes!");
    }

    public void powerOn() {
        isPoweredOn = true;
        System.out.println("Amplifier: Powered ON");
    }

    public void setBassBoost(boolean enabled) {
        this.bassBoost = enabled;
        System.out.println("Amplifier: Bass boost " + (enabled ? "ON" : "OFF"));
    }
}

