    public void cleanTables() {
        System.out.println("Waiter is cleaning tables.");
    }

    public void manageStaff() {
        System.out.println("Waiter does not manage staff.");
    }
}

class Manager implements RestaurantWorker {
    private boolean conflict = true;

    public void cookFood() {
        System.out.println("Manager does not cook.");
    }

    public void serveCustomers() {
        System.out.println("Manager does not serve.");
    }

    public void cleanTables() {
        System.out.println("Manager does not clean.");
    }

    public void manageStaff() {
        if (conflict) {
            System.out.println("Manager is resolving a team conflict.");
