interface GameCharacter {
    fun meleeAttack()
    fun castSpell()
    fun fly()
    fun defend()
}

abstract class BaseCharacter(private val name: String) : GameCharacter {
    abstract fun meleeAttack()
    abstract fun castSpell()
    abstract fun fly()
    abstract fun defend()
}

class Mage(name: String) : BaseCharacter(name) {
    private var mana: Int = 100

    override fun meleeAttack() {
        throw UnsupportedOperationException("Mage cannot perform melee attacks!")
    }

