    var dirtyItems = 25
    val isIndustrialGrade = true
    val dishTypes = arrayOf("plates", "cups", "utensils", "pots")

    override fun washDishes() {
        for (dishType in dishTypes) {
            if (dirtyItems > 0) {
                if (dishType == "pots" && isIndustrialGrade) {
                    for (cycle in 1..3) {
                        println("Industrial dishwasher cleaning $dishType cycle $cycle")
