    fun castSpell()
    fun fly()
    fun defend()
}

interface Weapon {
    fun slash()
    fun shoot()
    fun enchant()
    fun repair()
}

class Mage(private val name: String, private val level: Int) : GameCharacter {
    private var mana: Int = 100

    override fun meleeAttack() {
        throw UnsupportedOperationException("Mage cannot perform melee attacks!")
    }

    override fun castSpell() {
        if (mana < 10) {
            println("$name is out of mana!")
            return
        }

        when {
            level >= 5 && mana >= 30 -> {
                println("$name casts a powerful lightning storm!")
                mana -= 30
            }
            level >= 3 && mana >= 20 -> {
                println("$name casts a fireball spell!")
                mana -= 20
            }
            mana >= 10 -> {
                println("$name casts a basic magic missile!")
                mana -= 10
            }
        }
    }

    override fun fly() {
        if (level >= 2) {
            println("$name soars through the air with magic!")
        } else {
            println("$name is too inexperienced to fly!")
        }
    }

    override fun defend() {
        if (mana >= 15) {
            println("$name creates a magical barrier!")
            mana -= 15
        } else {
            println("$name dodges awkwardly!")
        }
    }
}

class Fighter(private val name: String, private val hasWeapon: Boolean) : GameCharacter {
    private var stamina: Int = 100

    override fun meleeAttack() {
        if (stamina < 5) {
            println("$name is too exhausted to fight!")
            return
        }

        if (hasWeapon) {
            when {
                stamina >= 20 -> {
                    println("$name performs a devastating combo attack!")
                    stamina -= 20
                }
                stamina >= 10 -> {
                    println("$name swings weapon with force!")
                    stamina -= 10
                }
                else -> {
                    println("$name makes a weak weapon strike!")
                    stamina -= 5
                }
            }
        } else {
            when {
                stamina >= 15 -> {
                    println("$name throws a powerful punch!")
                    stamina -= 15
                }
                else -> {
                    println("$name throws a basic punch!")
                    stamina -= 5
                }
            }
        }
    }

    override fun castSpell() {
        throw UnsupportedOperationException("Fighter cannot cast spells!")
    }

    override fun fly() {
        throw UnsupportedOperationException("Fighter cannot fly!")
    }

    override fun defend() {
        when {
            hasWeapon && stamina >= 10 -> {
                println("$name blocks with weapon!")
                stamina -= 10
            }
            stamina >= 5 -> {
                println("$name raises arms to defend!")
                stamina -= 5
            }
            else -> {
                println("$name barely manages to dodge!")
            }
        }
    }
}

class Sword(private var sharpness: Int, private val isMagic: Boolean) : Weapon {
    override fun slash() {
        when {
            sharpness > 70 -> println("Sword cuts cleanly through the target!")
            sharpness > 30 -> println("Sword makes a decent cut!")
            else -> println("Dull sword barely scratches the target!")
        }
    }

    override fun shoot() {
        throw UnsupportedOperationException("Sword cannot shoot!")
    }

    override fun enchant() {
        if (isMagic) {
            println("Magical sword glows with enhanced power!")
            sharpness += 10
        } else {
            throw UnsupportedOperationException("Non-magic sword cannot be enchanted!")
        }
    }

    override fun repair() {
        if (sharpness < 100) {
            sharpness = minOf(100, sharpness + 25)
            println("Sword has been sharpened and repaired!")
        } else {
            println("Sword is already in perfect condition!")
        }
    }
}

class Bow(private var arrows: Int, private var range: Int) : Weapon {
    override fun slash() {
        throw UnsupportedOperationException("Bow cannot slash!")
    }

    override fun shoot() {
        if (arrows <= 0) {
            println("No arrows left to shoot!")
            return
        }

        when {
            range > 80 -> println("Long-range shot hits the distant target!")
            range > 50 -> println("Medium-range shot finds its mark!")
            else -> println("Short-range shot barely reaches the target!")
        }
        arrows--
    }

    override fun enchant() {
        throw UnsupportedOperationException("Regular bow cannot be enchanted!")
    }

    override fun repair() {
        if (range < 100) {
            range = minOf(100, range + 15)
            println("Bow string tightened and wood polished!")
        } else {
            println("Bow is in excellent condition!")
        }
    }
}

