from abc import ABC, abstractmethod


class MusicPlayer(ABC):
    @abstractmethod
    def play(self):
        pass

    @abstractmethod
    def pause(self):
        pass

    @abstractmethod
    def stop(self):
        pass

    @abstractmethod
    def skip_track(self):
        pass

    @abstractmethod
    def eject(self):
        pass

    @abstractmethod
    def set_volume(self, volume):
        pass

    @abstractmethod
    def set_equalizer(self, preset):
        pass

    @abstractmethod
    def display_track_info(self):
        pass

    @abstractmethod
    def shuffle(self):
        pass

    @abstractmethod
    def repeat(self):
        pass


class AdvancedAudioDevice(ABC):
    @abstractmethod
    def connect_bluetooth(self):
        pass

    @abstractmethod
    def disconnect_bluetooth(self):
        pass

    @abstractmethod
    def pair_device(self, device_name):
        pass

    @abstractmethod
    def enable_microphone(self):
        pass

    @abstractmethod
    def disable_microphone(self):
        pass

    @abstractmethod
    def firmware_update(self):
        pass


class CDPlayer(MusicPlayer):
    def __init__(self):
        self.is_playing = False
        self.is_paused = False
        self.current_track = "Track 1"
        self.volume = 50
        self.shuffle_mode = False
        self.repeat_mode = False

    def play(self):
        self.is_playing = True
        self.is_paused = False
        print(f"CD Player: Playing {self.current_track}")

    def pause(self):
        if self.is_playing:
            self.is_paused = True
            self.is_playing = False
            print("CD Player: Paused")

    def stop(self):
        self.is_playing = False
        self.is_paused = False
        print("CD Player: Stopped")

    def skip_track(self):
        print("CD Player: Skipping to next track")
        track_number = int(self.current_track.split(" ")[1]) + 1
        self.current_track = f"Track {track_number}"

    def eject(self):
        self.stop()
        print("CD Player: CD ejected")

    def set_volume(self, volume):
        self.volume = volume
        print(f"CD Player: Volume set to {volume}")

    def set_equalizer(self, preset):
        print(f"CD Player: Equalizer set to {preset}")

    def display_track_info(self):
        print(f"CD Player: Current track - {self.current_track}, Volume: {self.volume}")

    def shuffle(self):
        self.shuffle_mode = not self.shuffle_mode
        print(f"CD Player: Shuffle {'ON' if self.shuffle_mode else 'OFF'}")

    def repeat(self):
        self.repeat_mode = not self.repeat_mode
        print(f"CD Player: Repeat {'ON' if self.repeat_mode else 'OFF'}")


class VinylPlayer(MusicPlayer):
    def __init__(self):
        self.is_playing = False
        self.is_paused = False
        self.current_song = "Side A"
        self.volume = 50

    def play(self):
        self.is_playing = True
        self.is_paused = False
        print(f"Vinyl Player: Playing {self.current_song}")

    def pause(self):
        if self.is_playing:
            self.is_paused = True
            self.is_playing = False
            print("Vinyl Player: Paused (needle lifted)")

    def stop(self):
        self.is_playing = False
        self.is_paused = False
        print("Vinyl Player: Stopped")

    def skip_track(self):
        raise NotImplementedError("Vinyl players cannot skip tracks - you must manually move the needle!")

    def eject(self):
        raise NotImplementedError("Vinyl players don't eject - you must manually remove the record!")

    def set_volume(self, volume):
        self.volume = volume
        print(f"Vinyl Player: Volume set to {volume}")

    def set_equalizer(self, preset):
        raise NotImplementedError("Vintage vinyl players don't have digital equalizers!")

    def display_track_info(self):
        print(f"Vinyl Player: Current side - {self.current_song}, Volume: {self.volume}")

    def shuffle(self):
        raise NotImplementedError("Cannot shuffle vinyl records!")

    def repeat(self):
        raise NotImplementedError("Vinyl players don't have automatic repeat!")


class Amplifier(MusicPlayer):
    def __init__(self):
        self.is_powered_on = False
        self.volume = 30
        self.equalizer_setting = "Flat"
        self.bass_boost = False

    def play(self):
        raise NotImplementedError("Amplifiers don't play music - they amplify audio signals!")

    def pause(self):
        raise NotImplementedError("Amplifiers don't control playback!")

    def stop(self):
        self.is_powered_on = False
        print("Amplifier: Powered OFF")

    def skip_track(self):
        raise NotImplementedError("Amplifiers don't control track selection!")

    def eject(self):
        raise NotImplementedError("Amplifiers don't have media to eject!")

    def set_volume(self, volume):
        if volume < 0:
            self.volume = 0
        elif volume > 100:
            self.volume = 100
        else:
            self.volume = volume

        if self.volume > 80:
            print("Amplifier: Warning - High volume!")
        print(f"Amplifier: Master volume set to {self.volume}")

    def set_equalizer(self, preset):
        preset_lower = preset.lower()
        if preset_lower in ["rock", "pop", "jazz", "classical"]:
            self.equalizer_setting = preset
        else:
            self.equalizer_setting = "Flat"
            print("Amplifier: Unknown preset. Defaulting to Flat.")
            return
        print(f"Amplifier: Equalizer set to {self.equalizer_setting}")

    def display_track_info(self):
        raise NotImplementedError("Amplifiers don't track music information!")

    def shuffle(self):
        raise NotImplementedError("Amplifiers don't control playback modes!")

    def repeat(self):
        raise NotImplementedError("Amplifiers don't control playback modes!")

    def power_on(self):
        self.is_powered_on = True
        print("Amplifier: Powered ON")

    def set_bass_boost(self, enabled):
        self.bass_boost = enabled
        print(f"Amplifier: Bass boost {'ON' if enabled else 'OFF'}")


class BluetoothSpeaker(MusicPlayer, AdvancedAudioDevice):
    def __init__(self):
        self.is_connected = False
        self.volume = 50

    def play(self):
        if self.is_connected:
            print("Bluetooth Speaker: Playing audio")
        else:
            raise RuntimeError("Bluetooth Speaker not connected")

    def pause(self):
        print("Bluetooth Speaker: Paused")

    def stop(self):
        print("Bluetooth Speaker: Stopped")

    def skip_track(self):
        print("Bluetooth Speaker: Skipping track")

    def eject(self):
        raise NotImplementedError("Bluetooth Speakers don't eject media")

    def set_volume(self, volume):
