        if self.energy_type == "Electric":
            print("Submarine battery level: 70%")
        else:
            raise UnsupportedOperationException("Non-electric submarine has no battery system")

    def maintain_engine(self):
        print("Maintaining nuclear reactor systems")

    def get_operator(self):
        return self.captain
