input:
interface GameCharacter {
    void meleeAttack();
    void castSpell();
    void fly();
    void defend();
}

interface Weapon {
    void slash();
    void shoot();
    void enchant();
    void repair();
}

interface Enemy {
    void attack();
    void fly();
    void poison();
    void summonMinions();
    void takeDamage(int[] damageValues);
}

class Mage implements GameCharacter {
    private String name;
    private int mana;
    private int level;

    public Mage(String name, int level) {
        this.name = name;
        this.mana = 100;
        this.level = level;
    }

    @Override
    public void meleeAttack() {
        throw new UnsupportedOperationException("Mage cannot perform melee attacks!");
    }

    @Override
    public void castSpell() {
        if (mana < 10) {
            System.out.println(name + " is out of mana!");
            return;
        }

        if (level >= 5 && mana >= 30) {
            System.out.println(name + " casts a powerful lightning storm!");
            mana -= 30;
        } else if (level >= 3 && mana >= 20) {
            System.out.println(name + " casts a fireball spell!");
            mana -= 20;
        } else if (mana >= 10) {
            System.out.println(name + " casts a basic magic missile!");
            mana -= 10;
        }
    }

    @Override
    public void fly() {
        if (level >= 2) {
            System.out.println(name + " soars through the air with magic!");
        } else {
            System.out.println(name + " is too inexperienced to fly!");
        }
    }

    @Override
    public void defend() {
        if (mana >= 15) {
            System.out.println(name + " creates a magical barrier!");
            mana -= 15;
        } else {
            System.out.println(name + " dodges awkwardly!");
        }
    }
}

class Fighter implements GameCharacter {
    private String name;
    private int stamina;
    private boolean hasWeapon;

    public Fighter(String name, boolean hasWeapon) {
        this.name = name;
        this.stamina = 100;
        this.hasWeapon = hasWeapon;
    }

    @Override
    public void meleeAttack() {
        if (stamina < 5) {
            System.out.println(name + " is too exhausted to fight!");
            return;
        }

        if (hasWeapon) {
            if (stamina >= 20) {
                System.out.println(name + " performs a devastating combo attack!");
                stamina -= 20;
            } else if (stamina >= 10) {
                System.out.println(name + " swings weapon with force!");
                stamina -= 10;
            } else {
                System.out.println(name + " makes a weak weapon strike!");
                stamina -= 5;
            }
        } else {
            if (stamina >= 15) {
                System.out.println(name + " throws a powerful punch!");
                stamina -= 15;
            } else {
                System.out.println(name + " throws a basic punch!");
                stamina -= 5;
            }
        }
    }

    @Override
    public void castSpell() {
        throw new UnsupportedOperationException("Fighter cannot cast spells!");
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Fighter cannot fly!");
    }

    @Override
    public void defend() {
        if (hasWeapon && stamina >= 10) {
            System.out.println(name + " blocks with weapon!");
            stamina -= 10;
        } else if (stamina >= 5) {
            System.out.println(name + " raises arms to defend!");
            stamina -= 5;
        } else {
            System.out.println(name + " barely manages to dodge!");
        }
    }
}

class Sword implements Weapon {
    private int sharpness;
    private boolean isMagic;

    public Sword(int sharpness, boolean isMagic) {
        this.sharpness = sharpness;
        this.isMagic = isMagic;
    }

    @Override
    public void slash() {
        if (sharpness > 70) {
            System.out.println("Sword cuts cleanly through the target!");
        } else if (sharpness > 30) {
            System.out.println("Sword makes a decent cut!");
        } else {
            System.out.println("Dull sword barely scratches the target!");
        }
    }

    @Override
    public void shoot() {
        throw new UnsupportedOperationException("Sword cannot shoot!");
    }

    @Override
    public void enchant() {
        if (isMagic) {
            System.out.println("Magical sword glows with enhanced power!");
            sharpness += 10;
        } else {
            throw new UnsupportedOperationException("Non-magic sword cannot be enchanted!");
        }
    }

    @Override
    public void repair() {
        if (sharpness < 100) {
            sharpness = Math.min(100, sharpness + 25);
            System.out.println("Sword has been sharpened and repaired!");
        } else {
            System.out.println("Sword is already in perfect condition!");
        }
    }
}

class Bow implements Weapon {
    private int arrows;
    private int range;

    public Bow(int arrows, int range) {
        this.arrows = arrows;
        this.range = range;
    }

    @Override
    public void slash() {
        throw new UnsupportedOperationException("Bow cannot slash!");
    }

    @Override
    public void shoot() {
        if (arrows <= 0) {
            System.out.println("No arrows left to shoot!");
            return;
        }

        if (range > 80) {
            System.out.println("Long-range shot hits the distant target!");
        } else if (range > 50) {
            System.out.println("Medium-range shot finds its mark!");
        } else {
            System.out.println("Short-range shot barely reaches the target!");
        }
        arrows--;
    }

    @Override
    public void enchant() {
        throw new UnsupportedOperationException("Regular bow cannot be enchanted!");
    }

    @Override
    public void repair() {
        if (range < 100) {
            range = Math.min(100, range + 15);
            System.out.println("Bow string tightened and wood polished!");
        } else {
            System.out.println("Bow is in excellent condition!");
        }
    }
}

class Orc implements Enemy {
    private String name;
    private int health;
    private int strength;

    public Orc(String name, int strength) {
        this.name = name;
        this.health = 100;
        this.strength = strength;
    }

    @Override
    public void attack() {
        if (strength > 50) {
            System.out.println(name + " delivers a crushing blow!");
        } else {
            System.out.println(name + " swings clumsily!");
        }
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Orc cannot fly!");
    }

    @Override
    public void poison() {
        throw new UnsupportedOperationException("Orc cannot poison enemies!");
    }

    @Override
    public void summonMinions() {
        throw new UnsupportedOperationException("Orc cannot summon minions!");
    }

    @Override
    public void takeDamage(int[] damageValues) {
        int totalDamage = 0;
        for (int i = 0; i < damageValues.length; i++) {
            if (damageValues[i] > 0) {
                totalDamage += damageValues[i];
                if (damageValues[i] > 20) {
                    System.out.println(name + " staggers from heavy damage!");
                }
            }
        }
        health -= totalDamage;
        System.out.println(name + " takes " + totalDamage + " damage! Health: " + health);

        if (health <= 0) {
            System.out.println(name + " has been defeated!");
        }
    }
}

class Dragon implements Enemy {
    private String name;
    private int health;
    private int firepower;
    private boolean canFly;

    public Dragon(String name, int firepower) {
        this.name = name;
        this.health = 200;
        this.firepower = firepower;
        this.canFly = true;
    }

    @Override
    public void attack() {
        if (firepower > 80) {
            System.out.println(name + " breathes devastating flames!");
        } else if (firepower > 50) {
            System.out.println(name + " breathes hot fire!");
        } else {
            System.out.println(name + " breathes weak flames!");
        }
    }

    @Override
    public void fly() {
        if (canFly && health > 50) {
            System.out.println(name + " soars majestically through the sky!");
        } else if (health <= 50) {
            System.out.println(name + " is too wounded to fly!");
        }
    }

    @Override
    public void poison() {
        throw new UnsupportedOperationException("Dragon doesn't use poison!");
    }

    @Override
    public void summonMinions() {
        throw new UnsupportedOperationException("Dragon doesn't summon minions!");
    }

    @Override
    public void takeDamage(int[] damageValues) {
        int totalDamage = 0;
        int criticalHits = 0;

        for (int i = 0; i < damageValues.length; i++) {
            if (damageValues[i] > 0) {
                int reducedDamage = Math.max(1, damageValues[i] - 5);
                totalDamage += reducedDamage;

                if (damageValues[i] > 30) {
                    criticalHits++;
                    System.out.println(name + " roars in pain from critical hit!");
                }
            }
        }

        health -= totalDamage;
        System.out.println(name + " takes " + totalDamage + " damage (" + criticalHits + " critical hits)! Health: " + health);

        if (health <= 0) {
            System.out.println(name + " crashes to the ground, defeated!");
            canFly = false;
        }
    }
}

class Spider implements Enemy {
    private String name;
    private int health;
    private boolean hasPoison;

    public Spider(String name) {
        this.name = name;
        this.health = 50;
        this.hasPoison = true;
    }

    @Override
    public void attack() {
        System.out.println(name + " bites with venomous fangs!");
    }

    @Override
    public void fly() {
        throw new UnsupportedOperationException("Spider cannot fly!");
    }

    @Override
    public void poison() {
        if (hasPoison) {
            System.out.println(name + " injects deadly venom!");
        } else {
            System.out.println(name + " has no poison left!");
        }
    }

    @Override
    public void summonMinions() {
        throw new UnsupportedOperationException("Spider cannot summon minions!");
    }

    @Override
    public void takeDamage(int[] damageValues) {
        int totalDamage = 0;
        for (int damage : damageValues) {
            if (damage > 0) {
                int amplifiedDamage = damage + (damage / 2);
                totalDamage += amplifiedDamage;

                if (damage > 10) {
                    System.out.println(name + " screeches in pain!");
                }
            }
        }

        health -= totalDamage;
        System.out.println(name + " takes " + totalDamage + " damage! Health: " + health);

        if (health <= 0) {
            System.out.println(name + " curls up and dies!");
        }
    }
}

