class CDPlayer(MusicPlayer):
    def __init__(self):
        self.is_playing = False
        self.is_paused = False
        self.current_track = "Track 1"
        self.volume = 50
        self.shuffle_mode = False
        self.repeat_mode = False

    def play(self):
        self.is_playing = True
        self.is_paused = False
        print(f"CD Player: Playing {self.current_track}")

    def pause(self):
        if self.is_playing:
            self.is_paused = True
            self.is_playing = False
            print("CD Player: Paused")

    def stop(self):
        self.is_playing = False
        self.is_paused = False
        print("CD Player: Stopped")

    def skip_track(self):
        print("CD Player: Skipping to next track")
        track_number = int(self.current_track.split(" ")[1]) + 1
        self.current_track = f"Track {track_number}"

    def eject(self):
        self.stop()
        print("CD Player: CD ejected")

    def set_volume(self, volume):
        self.volume = volume
        print(f"CD Player: Volume set to {volume}")

    def set_equalizer(self, preset):
        print(f"CD Player: Equalizer set to {preset}")

    def display_track_info(self):
        print(f"CD Player: Current track - {self.current_track}, Volume: {self.volume}")

    def shuffle(self):
        self.shuffle_mode = not self.shuffle_mode
        print(f"CD Player: Shuffle {'ON' if self.shuffle_mode else 'OFF'}")

    def repeat(self):
        self.repeat_mode = not self.repeat_mode
        print(f"CD Player: Repeat {'ON' if self.repeat_mode else 'OFF'}")


