                stamina -= 5;
            }
        }
        else
        {
            if (stamina >= 15)
            {
                Console.WriteLine(name + " throws a powerful punch!");
                stamina -= 15;
            }
            else
            {
                Console.WriteLine(name + " throws a basic punch!");
                stamina -= 5;
            }
        }
    }

    public void CastSpell()
    {
        throw new NotSupportedException("Fighter cannot cast spells!");
