        }
    }

    @Override
    public void fly() {
        System.out.println(name + " soars through the air with magic!");
    }

    @Override
    public void defend() {
        System.out.println(name + " creates a magical shield!");
    }
}

class Fighter implements GameCharacter {
    private String name;
    private int stamina;

    public Fighter(String name) {
        this.name = name;
        this.stamina = 100;
    }

    @Override
    public void meleeAttack() {
        if (stamina >= 10) {
            System.out.println(name + " swings sword with great force!");
            stamina -= 10;
        } else {
            System.out.println(name + " is too tired to attack!");
        }
